// Import the static JSON files that will be generated by generateStaticData.js
import structuresList from './structuresList.json';
import structuresInfo from './structuresInfo.local.json';

// Function to get the basic list of structures (used in StructureList.js)
export function getStructuresList() {
  return structuresList;
}

// Function to get detailed info for a specific structure (used in StructureInfo.js)
export function getStructureInfo(number) {
  return structuresInfo.find((structure) => structure.number === number);
}
